var _level = 0;

const log = function (level, reference, text, ...args) {
    console.log("[" + level + "][" + new Date().toISOString() + "] " + reference + ": " + text, ...args);
}

export class Logger {

    static get LEVEL() {
        return {
            SILENT: 0,
            ERROR: 1,
            INFO: 2,
            DEBUG: 3
        }
    }

    constructor(reference) {
        this.reference = reference;
    }

    static set level(level) {
        _level = level;
    }

    error(text, ...args) {
        if (_level >= Logger.LEVEL.ERROR) log("ERROR", this.reference, text, ...args);
    }

    info(text, ...args) {
        if (_level >= Logger.LEVEL.INFO) log("INFO", this.reference, text, ...args);
    }

    debug(text, ...args) {
        if (_level >= Logger.LEVEL.DEBUG) log("DEBUG", this.reference, text, ...args);
    }

}

export class Random {

    constructor(seed = new Date().getTime()) {
        this.mt = new MersenneTwister(seed);
    }

    bool() {
        return this.mt.random() * 100 < 50;
    }

    integer(min, max) {
        var _min = min || 0;
        var _max = max || 100;
        return Math.floor(this.mt.random() * (_max - _min + 1) + _min);
    }

    percentage(chance) {
        return this.mt.random() * 100 < chance;
    }

}

