export class Body {

    static get TYPE() {
        return {
            STATIC: Box2D.b2_staticBody,
            KINEMATIC: Box2D.b2_kinematicBody,
            DYNAMIC: Box2D.b2_dynamicBody
        }
    }

    static get STATE() {
        return {
            CREATED: 0,
            REGISTERED: 1
        }
    }

    static get FACTOR() {
        return 50;
    }

    constructor(type, shape, options = {}) {
        this._state = Body.STATE.CREATED;

        this.type = type;
        this.options = options;

        this.materialDef = new Box2D.b2FixtureDef();
        this.materialDef.set_shape(shape);
        if (options.density) this.materialDef.set_density(options.density);
        if (options.friction) this.materialDef.set_friction(options.friction);
        if (options.bounciness) this.materialDef.set_restitution(options.bounciness);
        if (options.isSensor) this.materialDef.set_isSensor(options.isSensor);

        this.bodyDef = new Box2D.b2BodyDef();
        this.bodyDef.set_type(type);
        if (options.preventTunneling) this.bodyDef.set_bullet(options.preventTunneling);
        if (options.allowRotation) this.bodyDef.set_fixedRotation(!options.allowRotation);
        if (options.angle) this.bodyDef.angle.set_angle(options.angle);

        this.body = null;
    }

    get state() {
        return this._state;
    }

    set state(state) {
        this._state = state;
    }

    get position() {
        return Vector2(
            this.body.GetPosition().get_x() * Body.FACTOR,
            this.body.GetPosition().get_y() * Body.FACTOR
        );
    }

    set position(position) {
        this.bodyDef.get_position().set_x(position.x / Body.FACTOR);
        this.bodyDef.get_position().set_y(position.y / Body.FACTOR);
    }

}

import { Box2D } from 'link/Box2D';
import { Body } from 'model/component/Body';

export class Box extends Body {

    constructor(type, width, height, options = {}) {
        var shape = new Box2D.b2PolygonShape();
        shape.SetAsBox(width / Body.FACTOR, height / Body.FACTOR);
        super(type, shape, options);
        this.width = width;
        this.height = height;
    }

    clone() {
        return new Box(this.type, this.width, this.height, this.options);
    }

}

import { Graphic } from 'model/component/Graphic';

function defaultDraw() {
    this.setFill("rgba(255, 45, 21, 0.4)");
    this.setStroke("rgba(255, 45, 21, 0.9)");
    this.circle();
    this.fill();
    this.stroke();
    this.line(this.width / 2, this.height / 2, 0, 0);
    this.stroke();
}

export class Canvas extends Graphic {

    constructor(width = 128, height = 128, draw = defaultDraw, { sceneId } = {}) {
        super(width, height, sceneId);
        this.draw = draw;
        this.draw.bind(this);
        this.canvas = null;
        this.context = null;
    }

    clear(color) {
        this.clearRect(0, 0, this.width, this.height, color);
    }

    clearRect(x, y, width, height, color) {
        this.context.clearRect(x, y, width, height);
        if (color) {
            this.context = this.setColor(color);
            this.context.fillRect(x, y, width, height);
        }
    }

    setColor(color) {
        this.context.strokeStyle = color;
        this.context.fillStyle = color;
    }

    setStroke(color) {
        this.context.strokeStyle = color;
    }

    setFill(color) {
        this.context.fillStyle = color;
    }

    fill(color) {
        this.context.fill();
    }

    stroke(color) {
        this.context.stroke();
    }

    clip() {
        this.context.clip();
    }

    line(x1, y1, x2, y2) {
        this.context.beginPath();
        this.context.moveTo(x1, y1);
        this.context.lineTo(x2, y2);
        this.context.closePath();
        this.context.stroke();
    }

    circle(x = this.width / 2, y = this.height / 2, radius = Math.min(x, y)) {
        this.context.beginPath();
