import { Box2D } from 'link/Box2D';
import { Body } from 'model/component/Body';

export class Circle extends Body {

    constructor(type, radius, options = {}) {
        var shape = new Box2D.b2CircleShape();
        shape.set_m_radius(radius / Body.FACTOR);
        super(type, shape, options);
        this.radius = radius;
    }

    clone() {
        return new Circle(this.type, this.radius, this.options);
    }

}

export class Graphic {

    static get STATE() {
        return {
            CREATED: 0,
            BUFFERING: 1,
            LOADED: 2,
            REGISTERED: 3,
            FAILED: -1
        }
    }

    constructor(width, height, { sceneId } = {}) {
        this._state = Graphic.STATE.CREATED;
        this.width = width;
        this.height = height;
        this.sceneId = sceneId;
        this.texture = null;
        this.material = null;
        this.geometry = null;
        this.mesh = null;
    }

    get state() {
        return this._state;
    }

    set state(state) {
        return this._state = state;
    }

    get loaded() {
        return this._state >= Graphic.STATE.LOADED;
    }

}

import { Graphic } from 'model/component/Graphic'

export class Sprite extends Graphic {

    constructor(source, width, height, { rows = 1, cols = 1, sceneId } = {}) {
        super(width, height, sceneId);
        this.source = source;
        this.w = width;
        this.h = height;
        this.rows = rows;
        this.cols = cols;
        this.sceneId = sceneId;
        this.numberOfFrames = rows * cols;
        this.row = 0;
        this.col = 0;
        this.frame = 1;
        this.lastUpdateTime = 0;
    }

    setFrame(frame = 1) {
        if (this.state === Graphic.STATE.REGISTERED) {
            this.frame = frame;
            this.col = (Math.ceil(this.frame / this.cols)) - 1;
            this.row = (this.frame - 1) % this.cols;
            this.texture.offset.x = this.row / this.cols;
            this.texture.offset.y = this.col / this.rows;
        }
    }

    nextFrame() {
        this.setFrame((this.frame % this.numberOfFrames) + 1);
    }

    show() {
        if (this.state === Graphic.STATE.REGISTERED) {
            this.mesh.material.visible = true;
        }
    }

    hide() {
        if (this.state === Graphic.STATE.REGISTERED) {
            this.mesh.material.visible = false;
        }
    }

    clone() {
        return new Sprite(this.source, this.w, this.h, { rows: this.rows, cols: this.cols, sceneId: this.sceneId });
    }

}

import { Three } from 'link/Three';

export class Camera2D {

    constructor(id, left, right, top, bottom, near, far) {
        this.id = id;
        this.cam = new Three.OrthographicCamera(left, right, top, bottom, near, far);
        this.cam.position.set(right, bottom, far);
