}

import { Vector3 } from 'commons/math/Vector3';

export class GameObject {

    constructor(name, {
        update = () => { },
        position = Vector3(0, 0, 0),
        rotation = Vector3(0, 0, Math.PI),
        scale = Vector3(1, 1, 1) } = {}) {

        this.uid = -1;

        this.name = name;
        this.update = update;
        this.position = position;
        this.rotation = rotation;
        this.scale = scale;

        this._axis = Vector3(0, 1, 0);
        this._direction = Vector3(0, 1, 0);
        this._children = new Array();
        this._components = new Array();
        this._inbox = new Array();
    }

    get children() {
        return this._children;
    }

    get components() {
        return this._components;
    }

    get messages() {
        return this._inbox.splice(0);
    }

    findChild(uid) {
        var child = this._children.find(go => go.uid === uid);
        if (child) return child;
        else return this._children.forEach(go => go.findChild(uid));
    }

    findComponent(type) {
        return this._components.find(component => component instanceof type);
    }

    attach(go) {
        this._children.push(go);
        return this;
    }

    include(component) {
        this._components.push(component);
        return this;
    }

    receive(message) {
        this._inbox.push(message);
    }

    lookAt(position) {
        this._direction.x = position.x - this.position.x;
        this._direction.y = position.y - this.position.y;
        this._direction.normalize();
        this.rotation.z = this.getEulerRotation();
    }

    getEulerRotation() {
        var angle = this._direction.angleTo(this._axis);
        return (this._direction.y * this._axis.x > this._direction.x * this._axis.y) ? angle : -angle;
    }

    rotateAroundPivot(pivot, radians) {
        var c = Math.cos(radians);
        var s = Math.sin(radians);
        var result = this.position.clone();

        var oldX = result.x - pivot.x;
        var oldy = result.y - pivot.y;

        var newX = oldX * c - oldy * s;
        var newY = oldX * s + oldy * c;

        result.x = newX + pivot.x;
        result.y = newY + pivot.y;

        this.position = result;
    }

    clone(name = this.name + '_copy') {
        var clone = new GameObject(name, { update: this.update, position: this.position.clone(), rotation: this.rotation.clone(), scale: this.scale.clone() });
        this._children.forEach(child => clone._children.push(child.clone()));
        this._components.forEach(component => clone._components.push(component.clone()));
        return clone;
    }
}

export class Message {

    static get TYPE() {
        return {
            GAME: 0,
            PHYSICS: 1
        }
    }

    constructor(sender, receiver, timestamp, type, content) {
        this.sender = sender;
        this.receiver = receiver;
        this.timestamp = timestamp;
        this.type = type;
        this.content = content;
    }

}

import { Box2D } from 'link/Box2D';

export class PhysicsEvent {

    constructor(type, contact, { manifold, impulse } = {}) {
        this.type = type;
        this.contact = Box2D.wrapPointer(contact, Box2D.b2Contact);
        this.manifold = manifold ? Box2D.wrapPointer(manifold, Box2D.b2Manifold) : undefined;
        this.impulse = impulse ? Box2D.wrapPointer(impulse, Box2D.b2ContactImpulse) : undefined;
    }

}

import { Howler } from 'link/Howler';
import { Audio } from 'model/component/Audio';
import { Logger } from 'commons/Logger';

export class AudioService {

    constructor() {
        this._logger = new Logger(AudioService.name);
    }

    update(audio) {
        if (audio.state === Audio.STATE.CREATED) {
            audio.state = Audio.STATE.BUFFERING;
            audio.sound = new Howler.Howl({
                src: [audio.source],
                autoplay: false,
                loop: audio.loop,
                onload: () => {
                    audio.state = Audio.STATE.LOADED;
                    this._logger.debug("audio loaded", audio.source);
                },
                onplay: () => {
                    audio.state = Audio.STATE.RUNNING;
                },
                onstop: () => {
                    audio.state = Audio.STATE.LOADED;
                },
                onend: () => {
                    audio.state = Audio.STATE.LOADED;
                }
            });
        }
    }

}

export class MessageService {

    constructor() {
        this.messages = new Array();
    }

    update(go) {
        this.messages.filter(msg => msg.receiver.uid === go.uid).forEach(msg => go.receive(msg));
        this.messages = this.messages.filter(msg => msg.receiver.uid !== go.uid);
    }

}

import { Body } from 'model/component/Body';
import { PhysicsEvent } from 'model/core/PhysicsEvent';
import { Vector2 } from 'commons/math/Vector2';
import { Box2D } from 'link/Box2D';
import { Logger } from 'commons/Logger';

export class PhysicsService {

    static get LISTENER() {
        return {
            BEGIN_CONTACT: 1, // 0001
            END_CONTACT: 2, // 0010
            BEGIN_END_CONTACT: 3, // 0011
            PRE_SOLVE: 4, // 0100
            POST_SOLVE: 8, // 1000
            ALL_LISTENERS: 15 //1111
        }
    }

    constructor(gravity, allowSleep, listeners) {
        this._logger = new Logger(PhysicsService.name);

        this.velocityIterations = 10;
        this.positionIterations = 10;
        this.clearForcesOnUpdate = false;
        this.listeners = listeners;

        this._events = new Array();
        this._world = new Box2D.b2World(new Box2D.b2Vec2(gravity.x, gravity.y), allowSleep);

        var listener = new Box2D.JSContactListener();

        listener.BeginContact = (this.listeners & PhysicsService.LISTENER.BEGIN_CONTACT) ?
            (contact) => this._events.push(new PhysicsEvent("BeginContact", contact)) : () => { };
        listener.EndContact = (this.listeners & PhysicsService.LISTENER.END_CONTACT) ?
            (contact) => this._events.push(new PhysicsEvent("EndContact", contact)) : () => { };
        listener.PreSolve = (this.listeners & PhysicsService.LISTENER.PRE_SOLVE) ?
            (contact, manifold) => this._events.push(new PhysicsEvent("PreSolve", contact, { manifold: manifold })) : () => { };
        listener.PostSolve = (this.listeners & PhysicsService.LISTENER.POST_SOLVE) ?
            (contact, impulse) => this._events.push(new PhysicsEvent("PostSolve", contact, { impulse: impulse })) : () => { };

        this._world.SetContactListener(listener);

        this._logger.debug("physics world ready with listeners", listener);
    }

    get events() {
        return this._events.splice(0);
    }

    destroy(body) {
        return this._world.DestroyBody(body.bodyDef);
    }

    update(body, go) {
        if (body.state === Body.STATE.REGISTERED) {
            go.position.x = body.position.x;
            go.position.y = body.position.y;

        } else if (body.state === Body.STATE.CREATED) {
            body.materialDef.set_userData(go.uid);
            body.position = Vector2(go.position.x, go.position.y);
            body.body = this._world.CreateBody(body.bodyDef);
            body.body.CreateFixture(body.materialDef);
            body.state = Body.STATE.REGISTERED;
        }
    }

    simulate(fps) {
        this._world.Step(1 / fps, this.velocityIterations, this.positionIterations);
        if (this.clearForcesOnUpdate) {
            this._world.ClearForces();
        }
    }

}

import { Graphic } from 'model/component/Graphic';
import { Sprite } from 'model/component/Sprite';
import { Canvas } from 'model/component/Canvas';
import { Camera2D } from 'model/core/Camera2D';
import { Three } from 'link/Three';
import { Logger } from 'commons/Logger';
import { GameState } from 'GameState';

const cameras = new Map();
const scenes = new Map();

export class RenderService {

    static get DEFAULT_SCENE_ID() {
        return 'default_scene_id';
    }

    static get DEFAULT_CAMERA_ID() {
        return 'default_camera_id';
    }

    constructor(width, height, targetWidth, targetHeight) {
        this._logger = new Logger(RenderService.name);

        GameState.width = width;
        GameState.height = height;

        var canvas = document.createElement("canvas");

        this._renderer = (canvas.getContext('webgl') || canvas.getContext('experimental-webgl'))
            ? new Three.WebGLRenderer({ antialias: true })
            : new Three.CanvasRenderer();
        this._renderer.setClearColor(0x000000, 1);

        this.createScene(RenderService.DEFAULT_SCENE_ID);
        this.createCamera(RenderService.DEFAULT_CAMERA_ID);
    }

    get domElement() {
        return this._renderer.domElement;
    }

    createScene(sceneId) {
        scenes.set(sceneId, new Three.Scene());
    }

    createCamera(cameraId, { width = GameState.width, height = GameState.height, sceneId = RenderService.DEFAULT_SCENE_ID } = {}) {
        if (scenes.has(sceneId)) {
            var camera = new Camera2D(cameraId, width / -2, width / 2, height / -2, height / 2, Math.max(width, height) / -2, Math.max(width, height) / 2);
            camera.sceneIds.add(sceneId);
            cameras.set(cameraId, camera);
        } else {
            throw Error("Invalid scene " + sceneId);
        }
    }

    update(component, position, rotation, scale) {
        if (component instanceof Sprite) {
            if (component.state === Graphic.STATE.REGISTERED) {
                component.mesh.position.set(position.x, position.y, position.z);
                component.mesh.rotation.set(rotation.x, rotation.y, rotation.z);
                component.mesh.scale.set(-scale.x, scale.y, scale.z);

            } else if (component.state === Graphic.STATE.LOADED) {
                scenes.get(component.sceneId).add(component.mesh);
                component.state = Graphic.STATE.REGISTERED;

            } else if (component.state === Graphic.STATE.CREATED) {
                component.sceneId = component.sceneId ? component.sceneId : RenderService.DEFAULT_SCENE_ID;
                component.state = Graphic.STATE.BUFFERING;
                new Three.TextureLoader().load(
                    component.source,
                    function (texture) { // load callback
                        component.texture = texture;
                        component.texture.wrapS = component.texture.wrapT = Three.ClampToEdgeWrapping;
                        component.texture.flipY = true;
                        component.texture.minFilter = Three.NearestFilter;
                        component.texture.offset.x = component.row / component.cols;
                        component.texture.offset.y = component.col / component.rows;
                        component.texture.repeat.set(1 / component.cols, 1 / component.rows);
                        component.material = new Three.MeshBasicMaterial({ map: component.texture, side: Three.FrontSide });
                        component.material.transparent = true;
                        component.geometry = new Three.PlaneBufferGeometry(component.w, component.h, 1, 1);
                        component.mesh = new Three.Mesh(component.geometry, component.material);
                        component.state = Graphic.STATE.LOADED;
                        this._logger.debug("texture loaded", component.source);
                    }.bind(this),
                    function (xhr) { // download callback
                        this._logger.debug("texture " + (xhr.loaded / xhr.total * 100) + "% loaded", component);
                    }.bind(this),
                    function (xhr) { // error callback
                        component.state = Graphic.STATE.FAILED;
                        this._logger.error("error while loading texture", component.source, xhr);
                    }.bind(this)
                );

            } else if (component.state === Graphic.STATE.FAILED) {
                throw Error("Sprite failed to load with source " + component.source);
            }

        } else if (component instanceof Canvas) {
            if (component.state === Graphic.STATE.REGISTERED) {
                component.context.imageSmoothingEnabled = false;
                component.context.webkitImageSmoothingEnabled = false;
                component.context.mozImageSmoothingEnabled = false;
                component.clear();
                component.draw();
                // This makes the textures created during execution to work properly
                //component.texture.needsUpdate = true;
                component.texture.image = component.canvas;
            } else if (component.state === Graphic.STATE.CREATED) {
                component.canvas = document.createElement('canvas');
                component.canvas.width = component.width;
                component.canvas.height = component.height;
                component.context = component.canvas.getContext("2d");
                component.texture = new Three.Texture(this.canvas);
                component.texture.flipY = true;
                component.texture.wrapS = component.texture.wrapT = Three.ClampToEdgeWrapping;
                component.texture.minFilter = Three.NearestFilter;
                component.material = new Three.MeshBasicMaterial({
                    map: component.texture,
                    side: Three.FrontSide
                });
                component.material.transparent = true;
                component.geometry = new Three.PlaneBufferGeometry(component.width, component.height, 1, 1);
                component.mesh = new Three.Mesh(component.geometry, component.material);

                scenes.get(RenderService.DEFAULT_SCENE_ID).add(component.mesh);

                component.state = Graphic.STATE.REGISTERED;
            }
        }
    }

    render() {
        this._renderer.setSize(GameState.width * GameState.ratio, GameState.height * GameState.ratio);
        cameras.forEach(camera => {
            camera.sceneIds.forEach(sceneId => {
                this._renderer.render(scenes.get(sceneId), camera.cam);
            });
        });
    }

}
